{{Production|input1=raw-lead|Input1Amount=2000|input2=coke-fuel|Input2Amount=168|time=182|Mamyflux=6MMF|output1=lead-ingot|Output1Amount=1000|input3=water|Input3Amount=5000|machine=Foundry}} 
{{Production|input1=raw-lead|Input1Amount=2000|input2=coal|Input2Amount=1680|time=182|Mamyflux=6MMF|output1=lead-ingot|Output1Amount=1000|input3=water|Input3Amount=5000|machine=Foundry}} 
